<script lang="ts">
    import { onMount } from "svelte";
    import Warp from "warpjs";

    onMount(() => {
        let offset = 0;
        
        const transformFn = (widthFactor: number, mid: number, amplitude: number, aO: number = 1) => {
            return ([x, y, oy]: [number, number, number]) => {
                const sinOffset = Math.sin(x / widthFactor + offset + aO) * aO;
                return [x, oy + mid + amplitude * sinOffset, oy];
            };
        };

        const marq3 = document.querySelectorAll<SVGElement>("#marq3 .jelly");
        const marq3Height = 230;
        const speed = 0.005;

        marq3.forEach((el) => {
            const warp = new Warp(el);

            warp.interpolate(4);
            warp.transform(([x, y]) => [x, y, y]);

            const widthFactor = el.clientWidth / 6.1;
            const amplitude = marq3Height / 2;
            const mid = marq3Height / 2;

            function animate() {
                warp.transform(transformFn(widthFactor, mid, amplitude));
                offset += speed;
                requestAnimationFrame(animate);
            }

            animate();
        });

        const marq2 = document.querySelectorAll<SVGElement>("#marq2 .jelly");
        const marq2Height = 189.5;

        marq2.forEach((el) => {
            const warp = new Warp(el);

            warp.interpolate(4);
            warp.transform(([x, y]) => [x, y, y]);

            const widthFactor = el.clientWidth / 6.1;
            const amplitude = marq2Height / 2;
            const mid = marq2Height / 2;

            function animate() {
                warp.transform(transformFn(widthFactor, mid, amplitude, Math.PI / 3));
                requestAnimationFrame(animate);
            }

            animate();
        });

        const marq1 = document.querySelectorAll<SVGElement>("#marq1 .jelly");
        const marq1Height = 135.5;

        marq1.forEach((el) => {
            const warp = new Warp(el);

            warp.interpolate(4);
            warp.transform(([x, y]) => [x, y, y]);

            const widthFactor = el.clientWidth / 6.1;
            const amplitude = marq1Height / 2;
            const mid = marq1Height / 2;

            function animate() {
                warp.transform(transformFn(widthFactor, mid, amplitude, Math.PI / 4.5));
                requestAnimationFrame(animate);
            }

            animate();
        });
    });
</script>

